import React, {useState, useEffect, useRef} from 'react';
import ChatBot from './chatbot'
import Assistant from './assistant'
import {chatPrompt, actionPrompt} from './prompt'
import { CircularProgress } from '@mui/material';

const parseMemories = (memories)=>{
    if(!memories) return {
        lastMemory : null,
        historyMemory :null
    }
    const steps = Object.keys({...memories}).map(Number)
    const lastStep = Math.max(...steps)
    const historyMemory = []
    Object.entries(memories).forEach(([step,value])=>{
        if (value && step != lastStep){
            const {scene, ...memo} = value
            historyMemory.push({[`step_${step}`]:memo})
        }
    });
    const lastMemory = {step:lastStep, ...memories[lastStep]}
    return {lastMemory,historyMemory}
}

// main function
export default function TravelAgent(props){

    const {input,config,setConfig,setOutput} = props;
    const BASEURL = window.secrets["LLM_BASE_URL"];
    const APIKEY = window.secrets["LLM_API_KEY"];
    const MODEL = window.secrets["LLM_MODEL"];
    const memories = input?.memories;
    const persona = input?.persona;
    const task = input?.task;
    const currentStep = useRef(0)
    const {lastMemory,historyMemory} = parseMemories(memories);
    const [loading,setLoading] = useState(false)
    const [response, setResponse] = useState(null)

    const context = []
    if (historyMemory) {
        context.push(`history meomories: ${JSON.stringify(historyMemory)}`)
    }
    if(lastMemory?.scene){
        context.push(`current scene you see:`);
        context.push(lastMemory?.scene);
    }

    const chatAssistant = new Assistant({
        baseUrl: BASEURL,
        model : MODEL,
        apiKey: APIKEY,
        systemPrompt : chatPrompt({persona,task}),
        context: context,
        responseFormat: "text"
    })

    const actionAssistant = new Assistant({
        baseUrl: BASEURL,
        model : MODEL,
        apiKey: APIKEY,
        systemPrompt : actionPrompt({persona,task}),
        responseFormat: "json_object"
    })

    useEffect(()=>{
        if(!(persona && task))return;
        if(lastMemory && lastMemory.step>currentStep.current){
        const {scene, ...lastAction} = lastMemory;
        const inputMessage = [
            `history meomories: ${JSON.stringify(historyMemory)}`,
            `last action: ${lastAction && JSON.stringify(lastAction) || null}`,
            `current scene you see:`,
            scene, 
            "What's your next action?:"
        ];
        setLoading(true);
        actionAssistant.chat(inputMessage)
            .then((response) => {
                const answer = JSON.parse(response);
                answer["step"] = lastMemory.step + 1;
                setOutput({
                    output: answer
                })
                setResponse(JSON.stringify(answer))
                setLoading(false)
            })
            .catch((e)=>{
                setOutput(null);
                setLoading(false);
                console.error(e);
            });
        currentStep.current += 1;
        }
    },[lastMemory,historyMemory])

    return (
    <div style={{
        display:"flex",
        height:"400px",
        alignItems:"center",
        justifyContent:"center"
    }}>
    {
        loading ? 
        (<CircularProgress sx={{color:"white"}}/>)
        :
        (<ChatBot 
            assistant = {chatAssistant} 
            response = {response}
        />)
    }
    </div>
    )
}
