const parseMessage = (messages) => {
  if (!messages) {
    return [];
  }
  return messages.map((msg) => {
    if (msg.role === 'AI') {
      return { role: 'assistant', content: msg.message };
    } else if (msg.role === 'system') {
      return { role: 'system', content: msg.message };
    } else {
      return { role: 'user', content: msg.message };
    }
  });
};

const isBase64Image = (str) => {
    return /^data:image\/[a-zA-Z]+;base64,/.test(str);
};

export default class Assistant {
  constructor(props) {
    // requried props: systemPrompt, apiKey, model, responseFormat 
    if (!props) return;
    this.props = props;
  }

  async chat(inputMessage, messageHistory = null) {

    const messages = [
      {
        role: 'system',
        content: this.props.systemPrompt || 'you are a helpful assistant',
      },
    ];
    if (messageHistory) {
      messages.push(...parseMessage(messageHistory));
    }
    if (this.props?.context){
        const contextToPush = Array.isArray(this.props?.context) ? this.props?.context : [this.props?.context];

      contextToPush.forEach((msg) => {
        messages.push(
          isBase64Image(msg)
            ? {
                role: 'user',
                content: [
                  { type: 'image_url', image_url: { url: msg } },
                ],
              }
            : { role: 'user', content: msg || '' }
        );
      });
    }
    if (inputMessage) {
      const messagesToPush = Array.isArray(inputMessage) ? inputMessage : [inputMessage];

      messagesToPush.forEach((msg) => {
        messages.push(
          isBase64Image(msg)
            ? {
                role: 'user',
                content: [
                  { type: 'image_url', image_url: { url: msg } },
                ],
              }
            : { role: 'user', content: msg || '' }
        );
      });
    }
    try {
      const response = await fetch(`${this.props?.baseUrl}/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${this.props?.apiKey}`,
        },
        body: JSON.stringify({
          model: this.props?.model || 'gpt-4o-mini',
          messages: messages,
          response_format: this.props?.responseFormat
            ? {
                type: this.props?.responseFormat,
              }
            : { type: 'text' },
          max_tokens: this.props?.maxTokens || 4096,
          temperature: this.props?.temperature || 0.8,
          presence_penalty: this.props?.presencePenalty || 0.0,
          frequency_penalty: this.props?.frequencyPenalty || 0.0,
        }),
      }).catch((error) => {
        throw new Error(error);
      });
      if (!response.ok) {
        throw new Error(response.status);
      }
      const data = await response.json();
      return data.choices[0].message.content;
    } catch (error) {
      return error;
    }
  }

  async *stream(inputMessage, messageHistory, signal) {
    const messages = [
      {
        role: 'system',
        content: this.props.systemPrompt || 'you are a helpful assistant',
      },
    ];
    if (messageHistory) {
      messages.push(...parseMessage(messageHistory));
    }
    if (this.props?.context){
        const contextToPush = Array.isArray(this.props?.context) ? this.props?.context : [this.props?.context];
      contextToPush.forEach((msg) => {
        messages.push(
          isBase64Image(msg)
            ? {
                role: 'user',
                content: [
                  { type: 'image_url', image_url: { url: msg } },
                ],
              }
            : { role: 'user', content: msg || '' }
        );
      });
    }
    if (inputMessage) {
      const messagesToPush = Array.isArray(inputMessage) ? inputMessage : [inputMessage];
      messagesToPush.forEach((msg) => {
        messages.push(
          isBase64Image(msg)
            ? {
                role: 'user',
                content: [
                  { type: 'image_url', image_url: { url: msg } },
                ],
              }
            : { role: 'user', content: msg || '' }
        );
      });
    }
    try {
      const response = await fetch(`${this.props?.baseUrl}/chat/completions`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${this.props?.apiKey}`,
        },
        body: JSON.stringify({
          model: this.props?.model || 'gpt-4o-mini',
          messages: messages,
          response_format: this.props?.responseFormat
            ? {
                type: this.props?.responseFormat,
              }
            : { type: 'text' },
          max_tokens: this.props?.maxTokens || 4096,
          temperature: this.props?.temperature || 0.8,
          presence_penalty: this.props?.presencePenalty || 0.0,
          frequency_penalty: this.props?.frequencyPenalty || 0.0,
          stream: true,
        }),
        signal,
      }).catch((error) => {
        throw new Error(error);
      });

      if (!response.ok) {
        throw new Error(response.status);
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder('utf-8');
      let buffer = '';

      while (true) {
        // Check if the signal is aborted
        if (signal.aborted) {
          reader.cancel(); // Cancel the reader
          return; // Exit the generator
        }

        const { done, value } = await reader.read();
        if (done) break;
        const chunk = decoder.decode(value, { stream: true });
        buffer += chunk;

        let boundary = buffer.indexOf('\n');
        while (boundary !== -1) {
          const line = buffer.slice(0, boundary).trim();
          buffer = buffer.slice(boundary + 1);

          if (line.startsWith('data:')) {
            const jsonData = line.slice(5).trim();
            if (jsonData === '[DONE]') {
              return; // End of the stream
            }
            if (jsonData) {
              try {
                const parsed = JSON.parse(jsonData);
                const content = parsed.choices[0]?.delta?.content;
                if (content) {
                  yield content; // Stream output to the caller
                }
              } catch (e) {}
            }
          }
          boundary = buffer.indexOf('\n');
        }
      }
    } catch (error) {
      yield error;
    }
  }
}
