import StopCircleIcon from '@mui/icons-material/StopCircle';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import { MessageLeft, MessageRight } from './MessageBox';

export default function ChatBot({assistant, response}) {
  const [isLoading, setIsLoading] = useState(false);
  const [historyMessages, setHistoryMessages] = useState([]);
  const [greedingMessage, setGreedingMessage] = useState('');
  const [inputMessage, setInputMessage] = useState('');
  const [currentMessage, setCurrentMessage] = useState('');
  const [controller, setController] = useState(null);

  const messageEndRef = useRef(null);

  const handleAbort = () => {
    try {
      controller.abort();
      setController(null);
      setIsLoading(false);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(()=>{
    setGreedingMessage(response)
  },[response])

  useEffect(() => {
    if (
      historyMessages.length > 0 &&
      historyMessages[historyMessages.length - 1].role === 'human'
    ) {
      setIsLoading(true);
      const inputMessage = historyMessages[historyMessages.length - 1].message;
      const newController = new AbortController();
      setController(newController);
      const signal = newController.signal;
      const readStream = async () => {
        let reply = '';
        try {
          for await (const chunk of await assistant.stream(
            inputMessage,
            historyMessages.slice(0,historyMessages.length - 1),
            signal
          )) {
            reply += chunk;
            setCurrentMessage({ role: 'AI', message: reply });
          }
          setIsLoading(false);
        } catch (e) {
          setIsLoading(false);
          setCurrentMessage({ role: 'AI', message: e });
        } finally {
          setHistoryMessages([
            ...historyMessages,
            { role: 'AI', message: reply },
          ]);
          setCurrentMessage('');
        }
      };
      readStream();
    }
  }, [historyMessages]);

  useEffect(() => {
    isLoading &&
      setCurrentMessage({
        role: 'AI',
        message: `thinking...`,
      });
  }, [isLoading]);

  useEffect(() => {
    messageEndRef.current?.scrollIntoView({
      behavior: 'smooth',
      block: 'nearest',
    });
  }, [historyMessages, currentMessage]);

  return (
    <Stack spacing={1} 
    width="100%" 
    height="100%"
    overflow={'auto'}
    padding={2}
    >
    <Stack
    spacing={1}
    sx={{
        overflowY: 'auto',
        width: '100%',
        height: '100%',
        p: 0,
        m: 0,
        userSelect: 'text',
        cursor: 'auto',
    }}
    className="nowheel"
    >
    <MessageLeft
    key="greeding"
    message={greedingMessage || `Hi~`}
    name={"AI"}
    avatar={null}
    />
    {historyMessages &&
        historyMessages.map((message, index) => {
        return message['role'] === 'human' ? (
            <MessageRight
            key={index}
            message={message['message']}
            name="human"
            avatar={null}
            />
        ) : (
            <MessageLeft
            key={index}
            message={message['message']}
            name={"travel agent"}
            avatar={null}
            />
        );
        })}
    {currentMessage && currentMessage.role === 'AI' && (
        <MessageLeft
        message={currentMessage.message}
        name={"travel agent"}
        avatar={null}
        />
    )}
    <div ref={messageEndRef} />
    </Stack>
    <OutlinedInput
    placeholder="Chat with Travel Agent..."
    width="100%"
    size="small"
    value={inputMessage}
    onChange={(e) => setInputMessage(e.target.value)}
    onKeyDown={(e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
        setHistoryMessages([
            ...historyMessages,
            { role: 'human', message: inputMessage },
        ]);
        setInputMessage('');
        }
    }}
    endAdornment={
        <InputAdornment position="end">
        <Stack direction="row" spacing={1}>
            {isLoading ? (
            <IconButton size="small" onClick={handleAbort}>
                <StopCircleIcon />
            </IconButton>
            ) : (
            <IconButton
                size="small"
                onClick={() => {
                setHistoryMessages([
                    ...historyMessages,
                    { role: 'human', message: inputMessage },
                ]);
                setInputMessage('');
                }}
            >
                <SendIcon />
            </IconButton>
            )}
            <IconButton
            size="small"
            onClick={() => {
                setGreedingMessage('');
                setHistoryMessages([]);
                setInputMessage('');
            }}
            >
            <AddCircleIcon />
            </IconButton>
        </Stack>
        </InputAdornment>
    }
    />
    </Stack>
  );
}
