import React, { useEffect, useState, useRef } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { Sky,PerspectiveCamera } from '@react-three/drei';
import Controls from './controls'
import Settings from './settings'

const Model = ({ action, model, setOutput }) => {
  const frameCounter = useRef(0);
  const currentStep = useRef(0);
  const { gl, camera, scene } = useThree();

  //init
  useEffect(()=>{
    setOutput(null)
  },[model])

  useFrame(() => {
    frameCounter.current += 1;
    if (frameCounter.current >= 10) {
      // Take a screenshot every 10 frames
        if (action?.step && action.step>currentStep.current && setOutput){
        gl.render(scene, camera);
        const canvas = gl.domElement;
        const dataUrl = canvas && canvas.toDataURL('image/png');
        currentStep.current += 1
        setOutput({
          output: {
            step: action.step,
            scene: dataUrl,
            action: action.action
          }
        })
        }
      frameCounter.current = 0; // Reset the counter
    }
  });
  return <primitive object={model} />;
};

const RhinoViewer = (props) => {
  const {input,config, setOutput} = props;
  const [model, setModel] = useState(null);
  const [initSettings, setInitSettings] = useState({
