import React, { useEffect, useState, useRef,useMemo } from 'react';
import { useFrame, useThree } from '@react-three/fiber';
import * as THREE from 'three';



const Controls = ({ model, speed,turnSpeed, action, actionTimeout=1000})=>{
  const { camera, scene } = useThree();
  const currentStep = useRef(0);
  const keysPressed = useRef({
    w: false,
    a: false,
    s: false,
    d: false,
    q: false,
    e: false,
  });

  const obstacles = useMemo(() => {
    const obstacles = [];
    if (model) {
      model.traverse((child) => {
        if (child.isMesh && child.userData.isObstacle) {
          obstacles.push(child);
        }
      });
    }
    return obstacles;
  }, [model]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (Object.keys(keysPressed.current).includes(key)) {
        keysPressed.current[key] = true;
      }
    };

    const handleKeyUp = (e) => {
      const key = e.key.toLowerCase();
      if (Object.keys(keysPressed.current).includes(key)) {
        keysPressed.current[key] = false;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  useEffect(() => {
  if (action?.step > currentStep.current && action?.action) {
    keysPressed.current[action.action] = true;
    currentStep.current += 1;
    setTimeout(() => {
      keysPressed.current[action.action] = false;
    }, actionTimeout);
  }
}, [action,actionTimeout]);


  useFrame((_, delta) => {
    const speed_ = speed || 10;
    const turnSpeed_ = turnSpeed*Math.PI  || Math.PI / 2; // radians per second

    let moved = false;

    // Handle turning first.
    if (keysPressed.current.q) {
      camera.rotateY(turnSpeed_ * delta);
      moved = true;
    }
    if (keysPressed.current.e) {
      camera.rotateY(-turnSpeed_ * delta);
      moved = true;
    }

    // Recalculate forward vector after turning.
    const forward = new THREE.Vector3();
    camera.getWorldDirection(forward);
    forward.y = 0;
    forward.normalize();

    const right = new THREE.Vector3();
    right.crossVectors(camera.up, forward).normalize();

    const moveWithCollisionCheck = (direction, distance) => {
      const rayOrigin = camera.position.clone();
      const raycaster = new THREE.Raycaster(
        rayOrigin,
        direction.clone().normalize(),
        0,
        distance
      );

      const intersects = raycaster.intersectObjects(obstacles);
      if (intersects.length === 0) {
        camera.position.add(direction.multiplyScalar(distance));
        return true;
      } else {
        const closest = intersects[0].distance;
        const allowedDistance = Math.max(closest - 1, 0); // 保持 1 m的安全距离
        camera.position.add(direction.normalize().multiplyScalar(allowedDistance));
        return allowedDistance > 0;
      }
    };

    if (keysPressed.current.w) {
      moved = moveWithCollisionCheck(forward.clone(), speed_ * delta) || moved;
    }
    if (keysPressed.current.s) {
      moved = moveWithCollisionCheck(forward.clone().negate(), speed_ * delta) || moved;
    }
    if (keysPressed.current.a) {
      moved = moveWithCollisionCheck(right.clone(), speed_ * delta) || moved;
    }
    if (keysPressed.current.d) {
      moved = moveWithCollisionCheck(right.clone().negate(), speed_ * delta) || moved;
    }
  });

  return null;
};

export default Controls;

