  const [showSettings, setShowSettings] = useState(false)
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0)

  // Update settings function
  const handleSettingChange = (e, settingName) => {
    const value = e.target.value || e.target.valueAsNumber;
    setInitSettings({
      ...initSettings,
      [settingName]: value
    });
  };

  const handleSliderChange = (settingName, newValue) => {
    setInitSettings({
      ...initSettings,
      [settingName]: newValue
    });
  };

  // Handler for file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setLoading(true)
      const url = URL.createObjectURL(file);
      const loader = new Rhino3dmLoader();
      loader.setLibraryPath('https://cdn.jsdelivr.net/npm/rhino3dm@8.4.0/');
      loader.load(
        url,
        (object) => {
          object.rotateX(-Math.PI / 2);
          object.traverse((child) => {
          if (child.isMesh) {
            // 为所有网格添加碰撞标识
            child.userData.isObstacle = true;
          }
          });
          setModel && setModel(object);
          setLoading(false)
          URL.revokeObjectURL(url);
        },
        (progress) => {
          setProgress(Math.floor((progress.loaded / progress.total) * 100));
        },
        (error) => {
          console.error(error);
          URL.revokeObjectURL(url);
