//entrypoint.js
import React, { useState, useEffect } from 'react';
import {Slider,TextField,Stack } from '@mui/material';

// main function
export default function CustomUI(props){
    const {input,config,setConfig,setOutput} = props; 

    const [maxSteps, setMaxSteps] = useState(10);
    const [value, setValue] = useState(maxSteps);

    const handleChange = (e)=>{
        setValue(e.target.value);
    }

    const handleMaxStepsChange = (e) => {
        const newMax = Number(e.target.value);
        setMaxSteps(newMax);
        if (value > newMax) {
            setValue(newMax); // Adjust value if it exceeds new max
        }
    }

    useEffect(()=>{
        const action = input?.input;
        if (action && action.step < maxSteps){
            setValue(action.step);
            setOutput({
                output: action
            });
        } else {
            setValue(1);
            setOutput({ output: {
                step: 1,
                action: 'w'
            }});
        }
    },[input, value, maxSteps]);

    return (
        <Stack 
        spacing={1}
        style={{
            padding: 10,
            paddingTop: 30
        }}>
            <TextField
                fullWidth
                label="Max Steps"
                size="small"
                type="number"
                variant="outlined"
                value={maxSteps}
                onChange={handleMaxStepsChange}
                InputProps={{style: { fontSize: 8}}}
            />
            <Slider
                value={value}
                onChange={handleChange}
                min={0}
                max={maxSteps}
                step={1}
            />
        </Stack>
    )
}
